import React, { Component } from "react";

import TypeFilter from "./TypeFilter.js";
import IssuerFilter from "./IssuerFilter.js";
import FeeFilter from "./FeeFilter.js";
import FilteredList from "./FilteredList.js";
import Sorter from "./Sorter.js";
import Cart from './Cart.js';

import './App.css';
import { CardTypes, Issuers, AnnualFees, SortTypes } from "./const.js";

import Grid from '@material-ui/core/Grid';



class App extends Component {

  constructor() {
    super();
    this.state = {
      cart: [],
      cardNames: [],
      filter: {
        type: CardTypes.ALL_TYPES,
        issuer: Issuers.ALL_ISSUERS,
        fee: AnnualFees.ALL_FEES,
      },
      sortType: SortTypes.NAME,
      totalFee: 0,


    };
  }

  changeType = event => {
    console.log("SDF");
    let newType = event.target.value;
    let newFilter = this.state.filter;
    newFilter["type"] = newType;
    this.setState({
      filter: newFilter,
    });

    console.log(this.state.filter);
  };

  changeIssuer = event => {
    let newIssuer = event.target.value;
    let newFilter = this.state.filter;
    newFilter["issuer"] = newIssuer;
    this.setState({
      filter: newFilter,
    });
  };

  changeFee = event => {
    let newFee = event.target.value;
    let newFilter = this.state.filter;
    newFilter["fee"] = newFee;
    this.setState({
      filter: newFilter,
    });
  };


  changeSortType = event => {
    let newSortType = event.target.value;
    this.setState({
      sortType: newSortType,
    });
  };



  addToCart = card => {
    const [dialogIsOpen, setDialogIsOpen] = React.useState(false);
    openDialog = () => setDialogIsOpen(true);
    closeDialog = () => setDialogIsOpen(false);

    let cart = this.state.cart;
    let cardNames = this.state.cardNames;
    if (card.name in cardNames) {
      return (
        <div>
          <OnlyOneCardDialog open={dialogIsOpen} onClose={closeDialog} />
        </div>
      );
    } else {
      cart.push(card);
      cardNames.push(card.name);
      let total = this.state.totalFee;
      this.setState({
        cardNames: cardNames,
        totalFee: total,
        cart: cart,
      });
    }
  };

  removeFromCart = card => {
    let total = this.state.totalFee;
    total = total - card.fee;
    delete cardNames[card.name];
    cart = cart.filter((x) => x != card);

    this.setState({
      totalFee: total,
      cart: cart,
    });
  };


  render() {
    return (
      <div>
        <h1 style={{ margin: 10 }}>Credit Cards</h1>
        <h2 style={{ margin: 10 }}>Find the best credit card for you!</h2>
        <div id="filters">
          <Grid style={{ margin: 10 }}>
            <Sorter changeSort={this.changeSortType} sortType={this.state.sortType}></Sorter>
            <TypeFilter changeType={this.changeType} filter={this.state.filter}></TypeFilter>
            <IssuerFilter changeIssuer={this.changeIssuer} filter={this.state.filter}></IssuerFilter>
            <FeeFilter changeFee={this.changeFee} filter={this.state.filter}></FeeFilter>
          </Grid>
        </div>

        <div style={{
          display: 'flex', flexDirection: 'row', flexWrap: 'wrap', padding: 5,
        }}>
          <FilteredList
            filter={this.state.filter}
            sortType={this.state.sortType}
            addItem={this.addToCart}
            removeItem={this.removeFromCart}
          />

          <Cart cart={this.state.cart}
            totalFee={this.state.totalFee}
            removeFromCart={this.removeFromCart}
          />
        </div>
      </div>
    );
  }
}

export default App;
