import React, { Component } from "react";

import TypeFilter from "./TypeFilter.js";
import IssuerFilter from "./IssuerFilter.js";

import './App.css';
import { CardTypes, Issuers, AnnualFees, Bonuses, SortTypes } from "./const.js";

import Grid from '@material-ui/core/Grid';



class App extends Component {

  constructor() {
    super();
    this.state = {
      applyList: [],
      filter: {
        cardtype: Cardtypes.ALL_TYPES,
        issuer: Issuers.ALL_ISSUERS,
        annualfee: AnnualFees.ALL_FEES,
      },
      sortType: SortTypes.NAME,
      totalFee: 0,


    };

    changeType = event => {
      let newType = event.target.value;
      let newFilter = this.state.filter;
      newFilter["cardtype"] = newType;
      this.setState({
        filter: newFilter,
      });
    };

    changeIssuer = event => {
      let newIssuer = event.target.value;
      let newFilter = this.state.filter;
      newFilter["issuer"] = newIssuer;
      this.setState({
        filter: newFilter,
      });
    };

    changeFee = event => {
      let newFee = event.target.value;
      let newFilter = this.state.filter;
      newFilter["annualfee"] = newFee;
      this.setState({
        filter: newFilter,
      });
    };


    changeSortType = EVENT => {
      let newSortType = event.target.value;
      this.setState({
        sortType: newSortType,
      });
    };
  }


  render() {
    return (
      <div>
        <div id="filters">
          <Grid>
            <Sorter changeSort={this.changeSortType} sortMethod={this.state.sortType}></Sorter>
            <TypeFilter changeType={this.changeType} filter={this.state.filter}></TypeFilter>
            <IssuerFilter changeIssuer={this.changeIssuer} filter={this.state.filter}></IssuerFilter>
            <FeeFilter changeFee={this.changeFee} filter={this.state.filter}></FeeFilter>
          </Grid>
        </div>




        <FilteredList list={productList} />
      </div>
    );
  }
}

export default App;
